import { Calendar, Views, dateFnsLocalizer } from "react-big-calendar";
import { format, parse, startOfWeek, getDay } from "date-fns";
import enUS from "date-fns/locale/en-US";
import "react-big-calendar/lib/css/react-big-calendar.css"; // You can override this with Tailwind later
import React, { useState } from "react";

const locales = {
  "en-US": enUS,
};

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

const events = [
  {
    title: "Manifest: Align with goals",
    start: new Date(),
    end: new Date(),
    allDay: true,
  },
];

export default function MyCalendar() {
  const [view, setView] = useState(Views.MONTH);

  return (
    <div className="h-[90vh]">
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        defaultView={view}
        onView={setView}
        style={{ height: "100%", backgroundColor: "white", padding: "1rem" }}
      />
    </div>
  );
}
